def is_prime(n):
    if n <= 1:
        return False
    if n == 2:
        return True
    if n % 2 == 0:
        return False
    for i in range(3, int(n**0.5) + 1, 2):
        if n % i == 0:
            return False
    return True

def factorial(n):
    if n == 0 or n == 1:
        return 1
    else:
        return n * factorial(n - 1)

# Alternative iterative solution:
def factorial_iterative(n):
    result = 1
    for i in range(1, n + 1):
        result *= i
    return result

def reverse_string(s):
    reversed_str = ''
    for char in s:
        reversed_str = char + reversed_str
    return reversed_str

# Alternative using slicing:
def reverse_string_slice(s):
    return s[::-1]

# Alternative using two pointers:
def reverse_string_pointers(s):
    s_list = list(s)
    left, right = 0, len(s_list) - 1
    while left < right:
        s_list[left], s_list[right] = s_list[right], s_list[left]
        left += 1
        right -= 1
    return ''.join(s_list)

def find_largest(lst):
    if not lst:
        return None
    largest = lst[0]
    for num in lst[1:]:
        if num > largest:
            largest = num
    return largest

# Example usage:
numbers = [3, 7, 2, 9, 1, 5]
print(find_largest(numbers))  # Output: 9
